mapper.select:
SELECT 
FROM gadm_region

JOIN to_tsquery(regexp_replace(quote_literal(unaccent(trim(#{query}))),'\s+',':*&','g')||':*') AS query ON query  @@ fulltext_search



    gadm_level = #{level}


    AND parent_id[gadm_level] = #{parentId}


ORDER BY ts_rank_cd(fulltext_search, query) DESC,  id

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT COUNT(*)
FROM gadm_region

  JOIN to_tsquery(regexp_replace(quote_literal(unaccent(trim(#{query}))),'\s+',':*&','g')||':*') AS query ON query  @@ fulltext_search



    gadm_level = #{level}


    AND parent_id[gadm_level] = #{parentId}
